+void QOrganizerJsonDbConverter::attendeeDetailToJsonDbObject(const QOrganizerEventAttendee& attendeeDetail, QVariantMap& object) const
+{
+    QVariantMap detailValues = attendeeDetail.values();
+    QMap<QString, QVariant>::const_iterator i = detailValues.constBegin();
+    while (i != detailValues.constEnd()) {
+        if (i.key() == QOrganizerEventAttendee::FieldName) {
+            QString name = i.value().toString();
+            if (!name.isEmpty())
+                object.insert(QOrganizerJsonDbStr::attendeeName, name);
+        } else if (i.key() == QOrganizerEventAttendee::FieldEmailAddress) {
+            QString email = i.value().toString();
+            if (!email.isEmpty())
+                object.insert(QOrganizerJsonDbStr::attendeeEmailAddress, email);
+        } else if (i.key() == QOrganizerEventAttendee::FieldContactId) {
+            QString contactId = i.value().toString();
+            if (!contactId.isEmpty())
+                object.insert(QOrganizerJsonDbStr::attendeeContactId, contactId);
+        } else if (i.key() == QOrganizerEventAttendee::FieldParticipationRole) {
+            int partRole = i.value().toInt();
+            if (QOrganizerEventAttendee::RoleNonParticipant > partRole && partRole > 0)
+                object.insert(QOrganizerJsonDbStr::attendeeParticipationRole, partRole);
+        } else if (i.key() == QOrganizerEventAttendee::FieldParticipationStatus) {
+            int partStatus = i.value().toInt();
+            if (QOrganizerEventAttendee::StatusCompleted > partStatus && partStatus > 0)
+                object.insert(QOrganizerJsonDbStr::attendeeParticipationStatus, partStatus);
+        }
+        i++;
+    }
