+#!/usr/bin/env perl
+use 5.010;
+use strict;
+use warnings;
+
+=head1 NAME
+
+22-testrunner-sync-output.t - test testrunner's --sync-output option
+
+=head1 SYNOPSIS
+
+  perl ./22-testrunner-sync-output.t
+
+This test will run the testrunner.pl script with and without sync-output
+and verify the order in which subprocess output is printed.
+
+=cut
+
+use Capture::Tiny qw( capture_merged );
+use File::Spec::Functions;
+use FindBin;
+use Readonly;
+use Test::More;
+use Data::Dumper;
+use English qw( -no_match_vars );
+
+# Perl for writing a few lines with a sleep between them
+Readonly my $PERL_DELAYED_OUTPUT => <<'END_PERL';
+$|++;
+for my $i (1..3) {
+    sleep 1;
+    print "Line $i\n";
+}
+END_PERL
+
+# Testrunner script
+Readonly my $TESTRUNNER => catfile( $FindBin::Bin, '..', 'testrunner.pl' );
+
+# This script
+Readonly my $THIS_SCRIPT => catfile( $FindBin::Bin, $FindBin::Script );
+
+# Test output of concurrent processes with no syncing
+sub test_concurrent_unsynced
+{
+    # Run 4 async PERL_DELAYED_OUTPUT through testrunner with no attempt to sync output
+    my @cmd_unsynced = ($EXECUTABLE_NAME, $THIS_SCRIPT, '-run-children');
+
+    # With no --sync-output, verify the output is interleaved
+    my $out_unsynced = capture_merged { system( @cmd_unsynced ) };
+    is( $out_unsynced, <<'END_OUT' );
+Line 1
+Line 1
+Line 1
+Line 1
+Line 2
+Line 2
+Line 2
+Line 2
+Line 3
+Line 3
+Line 3
+Line 3
+All children done.
+END_OUT
+
+    return;
+}
+
+# Test output of concurrent processes with syncing
+sub test_concurrent_synced
+{
+    # Run 4 async PERL_DELAYED_OUTPUT through testrunner and sync output
+    my @cmd_synced = ($EXECUTABLE_NAME, $THIS_SCRIPT, '-run-children', '--sync-output');
+
+    # With --sync-output, verify the output is NOT interleaved
+    my $out_synced = capture_merged { system( @cmd_synced ) };
+    is( $out_synced, <<'END_OUT' );
+Line 1
+Line 2
+Line 3
+Line 1
+Line 2
+Line 3
+Line 1
+Line 2
+Line 3
+Line 1
+Line 2
+Line 3
+All children done.
+END_OUT
+
+    return;
+}
+
+sub main
+{
+    test_concurrent_unsynced;
+    test_concurrent_synced;
+
+    done_testing;
+    return;
+}
+
+# Spawn a few concurrent $PERL_DELAYED_OUTPUT processes through testrunner.
+# Anything in @ARGV is passed to the testrunner.
+sub run_children
+{
+    my @pids;
+    for my $i (1..4) {
+        my $pid = fork();
+        if (0 == $pid) {
+            exec($EXECUTABLE_NAME, $TESTRUNNER, @ARGV, '--',
+                 $EXECUTABLE_NAME, '-e', $PERL_DELAYED_OUTPUT);
+            die "exec failed: $!";
+        }
+        else {
+            push @pids, $pid;
+        }
+    }
+    while (@pids) {
+        shift @pids;
+        waitpid(-1, 0);
+    }
+    print "All children done.\n";
+    return;
+}
+
+if (@ARGV && $ARGV[0] eq '-run-children') {
+    shift @ARGV;
+    run_children;
+}
+else {
+    main;
+}
