+class QWinIoCompletionPort : protected QThread
+{
+public:
+    QWinIoCompletionPort()
+        : hPort(INVALID_HANDLE_VALUE)
+    {
+        setObjectName(QLatin1String("I/O completion port thread"));
+        HANDLE hIOCP = CreateIoCompletionPort(INVALID_HANDLE_VALUE, NULL, NULL, 0);
+        if (!hIOCP) {
+            qWarning("CreateIoCompletionPort failed with error code %d.\n", GetLastError());
+            return;
+        }
+        hPort = hIOCP;
+    }
+
+    ~QWinIoCompletionPort()
+    {
+        PostQueuedCompletionStatus(hPort, 0, NULL, NULL);
+        QThread::wait();
+        CloseHandle(hPort);
+    }
+
+    void registerNotifier(QWinOverlappedIoNotifier *notifier)
+    {
+        HANDLE hIOCP = CreateIoCompletionPort(notifier->hFile, hPort, reinterpret_cast<ULONG_PTR>(notifier), 0);
+        if (!hIOCP) {
+            qDebug() << notifier->hFile << hPort << notifier;
+            qWarning("Can't associate file handle with I/O completion port. Error code %d.\n", GetLastError());
+            return;
+        }
+        mutex.lock();
+        notifiers.insert(notifier, QPointer<QWinOverlappedIoNotifier>(notifier));
+        mutex.unlock();
+        if (!QThread::isRunning())
+            QThread::start();
+    }
+
+    void unregisterNotifier(QWinOverlappedIoNotifier *notifier)
+    {
+        mutex.lock();
+        notifiers.remove(notifier);
+        mutex.unlock();
+    }
+
+protected:
+    void run()
+    {
+        DWORD dwBytesRead;
+        ULONG_PTR pulCompletionKey;
+        OVERLAPPED *overlapped;
+
+        forever {
+            BOOL success = GetQueuedCompletionStatus(hPort,
+                                                    &dwBytesRead,
+                                                    &pulCompletionKey,
+                                                    &overlapped,
+                                                    INFINITE);
+
+            DWORD errorCode = success ? ERROR_SUCCESS : GetLastError();
+            if (!success && !overlapped) {
+                printf("GetQueuedCompletionStatus failed with error code %d.\n", errorCode);
+                return;
+            }
+
+            if (success && !(dwBytesRead || pulCompletionKey || overlapped)) {
+                // We've posted null values via PostQueuedCompletionStatus to end this thread.
+                return;
+            }
+
+            QWinOverlappedIoNotifier *notifierRawPtr = reinterpret_cast<QWinOverlappedIoNotifier *>(pulCompletionKey);
+            mutex.lock();
+            QPointer<QWinOverlappedIoNotifier> notifier = notifiers.value(notifierRawPtr);
+            mutex.unlock();
+            if (!notifier.isNull())
+                notifier->notify(dwBytesRead, errorCode);
+        }
+    }
+
+
+private:
+    HANDLE hPort;
+    QHash<QWinOverlappedIoNotifier *, QPointer<QWinOverlappedIoNotifier> > notifiers;
+    QMutex mutex;
+};
+
+Q_GLOBAL_STATIC(QWinIoCompletionPort, iocp)
+
+QWinOverlappedIoNotifier::QWinOverlappedIoNotifier(QObject *parent)
+    : QObject(parent),
+      hFile(INVALID_HANDLE_VALUE),
+      lastNumberOfBytes(0),
+      lastErrorCode(ERROR_SUCCESS)
+{
+    hEvent = CreateEvent(NULL, TRUE, FALSE, NULL);
+    connect(this, &QWinOverlappedIoNotifier::_q_deferNotified,
+            this, &QWinOverlappedIoNotifier::notified, Qt::QueuedConnection);
+}
+
+QWinOverlappedIoNotifier::~QWinOverlappedIoNotifier()
+{
+    setEnabled(false);
+    CloseHandle(hEvent);
+}
+
+void QWinOverlappedIoNotifier::setHandle(HANDLE h)
+{
+    hFile = h;
+}
+
+void QWinOverlappedIoNotifier::setEnabled(bool enabled)
+{
+    if (enabled)
+        iocp()->registerNotifier(this);
+    else
+        iocp()->unregisterNotifier(this);
+}
+
+bool QWinOverlappedIoNotifier::waitForNotification(int msecs)
+{
+    DWORD result = WaitForSingleObject(hEvent, msecs == -1 ? INFINITE : DWORD(msecs));
+    switch (result) {
+    case WAIT_OBJECT_0:
+        return true;
+    case WAIT_TIMEOUT:
+        return false;
+    }
+
+    qWarning("QWinOverlappedIoNotifier::waitForNotification: "
+             "WaitForSingleObject failed with error code %d.", GetLastError());
+    return false;
+}
+
+bool QWinOverlappedIoNotifier::fetchResult(DWORD *numberOfBytesRead, DWORD *errorCode)
+{
+    Q_CHECK_PTR(numberOfBytesRead);
+    Q_CHECK_PTR(errorCode);
+
+    if (WaitForSingleObject(hEvent, 0) != WAIT_OBJECT_0)
+        return false;
+    *numberOfBytesRead = lastNumberOfBytes;
+    *errorCode = lastErrorCode;
+    ResetEvent(hEvent);
+    return true;
+}
+
+/*!
+  * Note: This function runs in the I/O completion port thread.
+  */
+void QWinOverlappedIoNotifier::notify(DWORD numberOfBytes, DWORD errorCode)
+{
+    lastNumberOfBytes = numberOfBytes;
+    lastErrorCode = errorCode;
+    emit _q_deferNotified();
+    SetEvent(hEvent);
+}
+
+QT_END_NAMESPACE
