+        function test_detailsReturnsListOfLengthOne() {
+            compare(contact20.details(ContactDetail.PhoneNumber).length, 1);
+        }
+
+        function test_detailsReturnsListContainingTheDetail() {
+            compare(contact20.details(ContactDetail.PhoneNumber)[0].number, contact20PhoneNumber.number);
+        }
+    }
