+
+void tst_QGeoManeuver::timeToNextInstruction()
+{
+    int time = 0;
+    qgeomaneuver->setTimeToNextInstruction(time);
+
+    QCOMPARE (qgeomaneuver->timeToNextInstruction(),time);
+
+    time = 35;
+
+    QVERIFY (qgeomaneuver->timeToNextInstruction() != time);
+
+    qgeomaneuver->setTimeToNextInstruction(time);
+    QCOMPARE (qgeomaneuver->timeToNextInstruction(),time);
+}
