+    QFETCH(double, latitude);
+    QFETCH(double, longitude);
+
+    qgeocoordinate = new QGeoCoordinate (latitude,longitude);
+
+    qgeomaneuver->setWaypoint(*qgeocoordinate);
+
+    QCOMPARE(qgeomaneuver->waypoint(),*qgeocoordinate);
