+// Not passing
+   QVERIFY(!(qgeomaneuver->operator ==(*qgeomaneuvercopy)));
+// Not passing
+   QVERIFY(qgeomaneuver->operator !=(*qgeomaneuvercopy));
+
+    *qgeomaneuvercopy = qgeomaneuvercopy->operator =(*qgeomaneuver);
+    QVERIFY(qgeomaneuver->operator ==(*qgeomaneuvercopy));
+    QVERIFY(!qgeomaneuver->operator !=(*qgeomaneuvercopy));
+
+    delete qgeomaneuvercopy;
